import { GET_RECIPES, GET_RECIPE_DETAIL, POST_RECIPE } from '../actions';

const initialState = {
  recipes: [],
  recipeDetail: {}
};

export default function reducer(state = initialState, action) {
  switch(action.type) {
    case GET_RECIPES: 
      return {
        ...state,
        recipes: action.payload,
      };
    case GET_RECIPE_DETAIL: 
      return {
        ...state,
        recipeDetail: action.payload,
      };
    case POST_RECIPE: 
      return {
        ...state,
        recipeDetail: action.payload,
      };
    default:
      return {
        ...state,
      };
  }
};
