import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setPage } from '../../redux/actions.js';
import * as S from './Paginator.styled.js';


export default function Paginator() {
  const [pages, setPages] = useState([]);
  const recipes = useSelector(state => state.recipes.displayed);
  const currentPage = useSelector(state => state.parameters.page);
  const dispatch = useDispatch();

  useEffect(() => {
    function pagesButtonsGenerator(recipes) {
      const pages = [];
      let i = 0;
      while (recipes.length > 0) {
        recipes = recipes.slice(9);
        pages.push(i++)
      }
      return pages;
    }
    setPages(pagesButtonsGenerator([...recipes]))
  }, [recipes]);

  function handleClick(currentPage) {
    dispatch(setPage(currentPage));
  };

  return (
    <S.Div> 
      { pages.length > 1 && pages.map((page, index) => {
        const selectedButtonStyle = index === currentPage 
          ? { backgroundColor: '#FD1F4A', color: 'white' }
          : undefined;
        return (
          <S.Button 
            key={ index }
            style={ selectedButtonStyle }
            onClick={ () => {handleClick(index)} }>
            { index + 1}
          </S.Button>
        );
      }) }
    </S.Div>
  )
};
