import styled from 'styled-components';

export const Recipe = styled.div`
	position: relative;
	display: flex;
	flex-direction: column;
	justify-content: space-between;
	background: #FAF5E6;
	height: 624px;
	width: 500px;
	margin: 25px;
	box-shadow: 10px 15px 20px 2px #222;
`

export const Image = styled.img`
	background-size: cover;
	min-height: 350px;
`

export const Info = styled.div`
	display: flex;
	flex-direction: column;
	justify-content: space-between;
`

export const Title = styled.h1`
	margin: 20px 30px 10px;
	color: #2D2C2E;
	text-align: left;
	font-size: 1.4rem;
	font-weight: normal;
	letter-spacing: 0.1em;
	text-transform: uppercase;
`

export const Score = styled.div`
	font-size: 40px;
	margin: 10px;

	&:before {
		content: '★★★★★';
		background: ${(props) => `linear-gradient(90deg, #FBBD0D ${props.percentage}%, #2D2C2E ${props.percentage}%)`};
		background-clip: text;
		-webkit-background-clip: text;
		-webkit-text-fill-color: transparent;
}
`

export const Diets = styled.ul`
	display: flex;
	flex-wrap: no-wrap;
	margin: 5px 5px 10px;
	width: auto;
  overflow-x: scroll;

	::-webkit-scrollbar {
		display: none;  /* Hide scrollbar in Chrome, Safari and Opera. */
		scrollbar-width: none;  /* Hide in Firefox. */
		-ms-overflow-style: none;  /* Hide in IE and Edge. */
	}
`

export const Diet = styled.li`
	flex-shrink: 0;
	padding: 2px 10px;
	margin: 5px;
	width: auto;
	background-color: #2D2C2E;
	color: #FAF5E6;
	border-radius: 20px;
	font-size: 0.8rem;
	text-align: center;
`

export const DetailButton = styled.button`
	padding: 20px;
	width: 100%;
	background-color: #FD1F4A;
	color: #FAF5E6;
	text-align: center;
	letter-spacing: 0.1em;
	transition: 250ms;

	&:hover {
		background: #FBBD0D;
	}
`

