import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components';
import { setPage } from '../actions';


export default function Paginator() {
  const [pages, setPages] = useState([]);
  const { cards, currentPage } = useSelector(state => state);
  const dispatch = useDispatch();

  useEffect(() => {
    function paginatorFunction(cards) {
      const pages = [];
      let i = 0;
      while (cards.length > 0) {
        cards = cards.slice(9);
        pages.push(i++)
      }
      return pages;
    }

    setPages(paginatorFunction([...cards]))
  }, [cards])


  function handleClick(index) {
    dispatch(setPage(index));
  }

  return (
    <Div> 
      { pages.length > 1 && pages.map((page, index) => {
        const selectedButtonStyle = index === currentPage ? {backgroundColor: 'grey'} : {};
        return (
          <Button 
            key={ index }
            style={ selectedButtonStyle }
            onClick={ () => {handleClick(index)} }>
            { index + 1}
          </Button>
        );
      }) }
    </Div>
  )
};

const Div = styled.div`
  position: absolute;
  left: 0px;
  top 200px;

  font-size: 20px;
  color: black;
`

const Button = styled.button`
  position: relative;

  margin: 10px;
  padding: 5px;
  background-color: deepPink;
`

const selectedStyle = {
  backgroundColor: 'gray',
}
