import styled from 'styled-components';
import image from '../../media/recipe-detail.jpg'
import { colors } from '../GlobalStyle.styled';

export const Container = styled.div`
	padding: 120px 120px 20px;
	background-image: url(${image});
	background-repeat: no-repeat;
	background-size: 100% 1780px;;
	background-attachment: static;
	background-color: red;
`

export const RecipeDetail = styled.div`
	height: auto;
	width: 800px;
	display: flex;
	flex-direction: column;
  box-shadow: 0px 8px 16px 0px rgba(155,155,155,0.4);
	background-color: ${colors.WHITE};
	color: ${colors.BLACK};
`

export const Image = styled.img`
	max-height: 600px;
	background-size: cover;
`

export const Info = styled.div`
	display: flex;
	flex-direction: column;
	justify-content: flex-start;
`

export const Title = styled.h1`
	margin: 80px 40px 5px;
	text-align: left;
	font-size: 1.8rem;
	font-weight: normal;
	letter-spacing: 0.1em;
	text-transform: uppercase;
`

export const Scores = styled.div`
	display: flex;
`

export const Score = styled.p`
	margin: 15px 20px;
	font-size: 1.2rem;

	&:after {
		padding: 5px;
		content: '★★★★★';
		font-size: 1.4rem;
		background: ${(props) => `linear-gradient(90deg, ${colors.YELLOW} ${props.percentage}%, ${colors.BLACK} ${props.percentage}%)`};
		background-clip: text;
		-webkit-background-clip: text;
		-webkit-text-fill-color: transparent;
	}
`

export const HealthScore = styled.i`
	margin: 15px 20px;
	padding: 5px;
	color: ${colors.BLACK};
	opacity: 0.6
`

export const Diets = styled.ul`
	margin: 10px 15px;
	width: auto;
	display: flex;
	flex-wrap: wrap;
`

export const Diet = styled.li`
	flex-shrink: 0;
	margin: 5px 5px;
	padding: 2px 10px;
	width: auto;
	font-size: 0.8rem;
	text-align: center;
	border-radius: 20px;
	background-color: ${colors.BLACK};
	color: ${colors.WHITE};
`

export const SubTitle = styled.h1`
	margin: 20px;
`

export const Text = styled.div`
	margin: 15px;
	padding: 10px 10px 10px 34px;
	// min-height: 250px;
	max-height: 250px;
	background-color: #EAE5D6;
	overflow-y: scroll;

	::-webkit-scrollbar {
		width: 12px;
	}

	::-webkit-scrollbar-thumb {
		background-color: ${colors.BLACK}; 
		border-radius: 10px;
	}
`
