import axios from 'axios';

export const SET_RECIPES = 'SET_RECIPES';
export const SET_RECIPE_DETAIL = 'SET_RECIPE_DETAIL';

export function getRecipes(query) {
  return async (dispatch) => {
    try {
      const result = await axios.get(`http://localhost:3001/recipes?name=${query}`);
      const recipes = result.data;
      dispatch({
        type: SET_RECIPES,
        payload: recipes,
        statePropperty: 'recipes',
      });
    } 

    catch(error) {
      console.log(error);
    }
  }
};

export function getRecipeDetail(id) {
  return async function(dispatch) {
    try {
      const result = await axios.get(`http://localhost:3001/recipes/${id}`); 
      const recipeDetail = result.data;
      dispatch({
        type: SET_RECIPE_DETAIL,
        payload: recipeDetail,
      });
    }

    catch(error) {
      console.log(error);
    }
  }
};

export function postRecipe(details) {
  return async function(dispatch) {
    try {
      const result = await axios({
        method: 'post',
        url: 'http://localhost:3001/recipes',
        headers: {}, 
        data: details
      });

      const newRecipe = result.data;
      dispatch({
        type: SET_RECIPE_DETAIL,
        payload: newRecipe,
      });
    }

    catch(error) {
      console.log(error);
    }
  }
};
