import axios from 'axios';

export const GET_RECIPES = 'GET_RECIPES';
export const GET_DIETS = 'GET_DIETS';
export const CHANGE_FILTERS = 'CHANGE_FILTERS';
export const FILTER_RECIPES = 'FILTER_RECIPES';
export const SORT_RECIPES = 'SORT_RECIPES';
export const SET_PAGE = 'SET_PAGE';
export const SET_LOADING = 'SET_LOADING'
export const SET_NOT_FOUND = 'SET_NOT_FOUND'

export function getDiets() {
  return async function(dispatch) {
    try {
      const response = await axios.get(`http://localhost:3001/diets`);
      const diets = response.data;
      dispatch({
        type: GET_DIETS,
        payload: diets,
      });
    } catch(error) {
      console.error(error);
    }
  }
}

export function getRecipes(query) {
  return async function(dispatch) {
    try {
      dispatch(setLoading(true));
      const response = await axios.get(`http://localhost:3001/recipes?title=${query}`);
      const recipes = (response.data.length > 0) ? response.data : 'not found';
      dispatch({
        type: GET_RECIPES,
        payload: recipes 
      });
      dispatch(setLoading(false));
    } catch(error) {
      console.error(error);
      dispatch(setLoading(false));
    }
  }
};

export function changeFilters(filter) {
  return {
    type: CHANGE_FILTERS,
    payload: filter
  }
}

export function filterRecipes() {
  return {
    type: FILTER_RECIPES,
  }
}

export function sortRecipes(sortCriteria) {
  return {
    type: SORT_RECIPES,
    payload: sortCriteria,
  }
};

export function setPage(pageNumber) {
  return {
    type: SET_PAGE,
    payload: pageNumber,
  }
}

export function setLoading(state) {
  return {
    type: SET_LOADING,
    payload: state
  }
}

export function setNotFound(state) {
  return {
    type: SET_NOT_FOUND,
    payload: state
  }
}
