import React, {useEffect, useState} from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';


export default function CreateRecipe() {
  const navigate = useNavigate();
  const [details, setDetails] = useState({
    title: '',
    summary: '',
    score: '',
    healthScore: '',
    instructions: '',
  });

  function handleChange(event) {
    setDetails({
      ...details,
      [event.target.name]: event.target.value,
    })
  } 

  async function handleSubmit(e) {
    e.preventDefault();
    const HardCoded = { title: 'Asado', summary: 'God', score: 10, healthScore: 0, instructions: 'Asar asado',};

    try {
      const result = await axios({
        method: 'post',
        url: 'http://localhost:3001/recipes',
        headers: {}, 
        data: HardCoded,
        // data: details
      });
      const newRecipe = result.data;
      console.log(result.data);
      navigate(`/recipe/detail/${ newRecipe.id }`)
    } catch(error) {
      console.log(error);
    }
  }

  return (
    <Form onSubmit={ handleSubmit }> 
      <label htmlFor='title'>Title: </label>
      <Input name='title' value={ details.title } onChange={ handleChange }/> 

      <label htmlFor='summary'>Summary: </label>
      <Input name='summary' value={ details.summary } onChange={ handleChange }/> 

      <label htmlFor='score'>Score: </label>
      <Input name='score' value={ details.score } onChange={ handleChange }/> 

      <label htmlFor='healthScore'>healthScore: </label>
      <Input name='healthScore' value={ details.healthScore } onChange={ handleChange }/> 

      <label htmlFor='instructions'>Instructions: </label>
      <Input name='instructions' value={ details.instructions } onChange={ handleChange }/> 

      <Button>
        Submit
      </Button>
    </Form>
  )
};

const Form = styled.form`
  position: relative;
  top: 200px;
  left: 500px;

  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  height: 400px;
  width: 1000px;
  background-color: cyan;
`;

const Input = styled.input`
  display: block;
  position: relative;

  height: 50px;
  width: 200px;
  color: black;
  border: solid black;
  margin: 10px;
`

const Button = styled.button`
  position: relative;

  height: 50px;
  width: 50px;
  color: black;
  border: solid black;
  background-color: violet;
  margin: 10px;
  padding: 10px;
`
