import { GET_CARDS, GET_ALL_FILTERS, CHANGE_APPLIED_FILTERS, CHANGE_CURRENT_PAGE, FILTER_CARDS, SORT_CARDS } from '../actions';

const initialState = {
  query: '',
  cards: [],
  allFilters: [],
  appliedFilters: [],
  currentPage: 0,
  filteredCards: [],
  sortCriteria: 'none',
};

export default function reducer(state = initialState, { type, payload}) {
  switch(type) {
    case GET_CARDS: 
      payload.cards[0].map(dbRecipe => ({...dbRecipe, diets: dbRecipe.diets.map(diet => {
        console.log(dbRecipe)
        console.log(dbRecipe.diets)
        console.log(diet.name)
        return diet.name
      })}))
      console.log(payload.cards[0])
      return {
        ...state,
        query: payload.query,
        cards: [...payload.cards[0], ...payload.cards[1]],
        currentPage: 0,
      };

    case GET_ALL_FILTERS:
      return {
        ...state,
        allFilters: payload,
      }

    case CHANGE_APPLIED_FILTERS: 
      return {
        ...state,
        appliedFilters: changeAppliedFiltersManager([...state.appliedFilters], payload)
      }

    case CHANGE_CURRENT_PAGE:
      return {
        ...state,
       currentPage: payload,
      }

    case FILTER_CARDS:
      return {
        ...state,
        filteredCards: filterCardsManager([...state.cards], [...state.appliedFilters])
      }

    case SORT_CARDS:
      return {
        ...state,
        currentPage: 0,
        filteredCards: sortCardsManager([...state.filteredCards], payload),
        sortCriteria: payload,
      }

    default:
      return {
        ...state,
      };
  }
};

function changeAppliedFiltersManager(appliedFilters, filter) {
  appliedFilters.includes(filter) 
    ? appliedFilters.splice(appliedFilters.indexOf(filter), 1) 
    : appliedFilters.push(filter)
  return appliedFilters;
}

function filterCardsManager(cards, appliedFilters) {
  return cards.filter(card => {
    return appliedFilters.every(filter => card.diets.includes(filter))
  })
};

function sortCardsManager(array, sortCriteria) {
  switch(sortCriteria) {
    case 'az': return array.sort((x, y) => x.title.localeCompare(y.title));
    case 'za': return array.sort((x, y) => y.title.localeCompare(x.title));
    case 'score': return array.sort((x, y) => y.score - x.score);
    default: return array;
  }
};
