import styled from 'styled-components';

export const SearchBar = styled.form`
  display: flex;
  align-items: center;
  width: 500px;
  height: 50px;
  border-radius: 5px;
  background-color: #800040;
`;

export const Query = styled.input`
  height: 100%;
  width: 100%;
  padding: 0px 40px;
  color: #fff;
`;

export const Button = styled.button`
  display: flex;
  align-items: center;
  width: 50px;
  height: 50px;
  cursor: pointer;
`;

export const MagnifyingGlass = styled.svg`
  width: 24px;
  height: 24px;
  color: #fff;
  fill: currentColor;
`;
