import { GET_CARDS, SET_PAGE, SORT_CARDS } from '../actions';

const initialState = {
  cards: [],
  currentPage: 0,
  sortCriteria: 'none',
};

export default function reducer(state = initialState, { type, payload}) {
  switch(type) {
    case GET_CARDS: 
      return {
        ...state,
        cards: payload,
        pages: paginatorFunction(payload),
        currentPage: 0,
      };

    case SET_PAGE:
      return {
        ...state,
       currentPage: payload,
      }

    case SORT_CARDS:
      return {
        ...state,
        cards: sortFunction([...state.cards], payload),
        currentPage: 0,
        sortCriteria: payload,
      }

    default:
      return {
        ...state,
      };
  }
};

function paginatorFunction(array) {
  const pages = [];
  let i = 0;
  while (array.length > 0) {
    array = array.slice(9);
    pages.push(i++)
  }
  return pages;
}

function sortFunction(array, sortCriteria) {
  switch(sortCriteria) {
    case 'az': return array.sort((x, y) => x.title.localeCompare(y.title));
    case 'za': return array.sort((x, y) => y.title.localeCompare(x.title));
    case 'score': return array.sort((x, y) => y.score - x.score);
    default: return array;
  }
}
