import React, {useEffect} from 'react';
import { useSelector, useDispatch} from 'react-redux'
import styled from 'styled-components'

import { filterCards, changeAppliedFilters } from '../actions';


export default function Filters() {
  const { query, allFilters, appliedFilters } = useSelector(state => state);
  const dispatch = useDispatch();

  function handleClick(filter) {
    dispatch(changeAppliedFilters(filter));
    dispatch(filterCards());
  }

  return (
    <Form> 
      { query && allFilters.map((filter) => {
          return (
            <div><label for={ filter.id }>
              { filter.name }
              <Input type='checkbox' key={ filter.id } onClick={ () => handleClick(filter.name) }/> 
            </label><br/></div>
          )
        }) 
      }
    </Form>
  )
};

const Form = styled.form`
  top: 300px;
  left: 0px;

  display: flex;
  align-items: center;
  height: 1000px;
  font-size: 15px;
  color: black;
  background-color: greenYellow;
`

const Input = styled.input`
  display: block;
  color: black;
`
