import React, {useEffect} from 'react';
import { useSelector, useDispatch} from 'react-redux'
import styled from 'styled-components'

import { filterCards, changeAppliedFilters } from '../actions';


export default function Filters() {
  const { query, allFilters, appliedFilters } = useSelector(state => state);
  const dispatch = useDispatch();

  function handleClick(event, filter) {
    event.preventDefault();
    dispatch(changeAppliedFilters(filter));
    dispatch(filterCards());
  }

  return (
    <Form> 
      { query && allFilters.map((filter) => {
          return (
            <button key={ filter.id } onClick={ (event) => handleClick(event, filter.name) }> 
              { filter.name } 
            </button>
          )
        }) 
      }
    </Form>
  )
};

const Form = styled.form`
  position: absolute;
  top: 300px;
  left: 0px;

  font-size: 30px;
  color: black;
  background-color: greenYellow;
`
