import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import axios from 'axios';
import * as S from './RecipeDetail.styled.js'

export default function RecipeDetail() {
  const { id } = useParams();
  const [details, setDetails] = useState({
    title: '',
    image: '',
    diets: [],
    summary: '',
    score: '',
    healthScore: '',
    instructions: [],
  });

  useEffect(() => {
    (async function(id) {
      try {
        const result = await axios.get(`http://localhost:3001/recipes/${id}`); 
        // Map diets property of db recipes from an array of objects to an array of strings.
        result.data.diets = !id.includes('EXTERNAL_') ? result.data.diets?.map(diet => diet.name) : result.data.diets;
        setDetails({
          ...details,
          ...result.data, 
        });
      } catch(error) {
        console.log(error);
      }
    })(id);
  }, [])

  return (
    <S.RecipeDetail>
      <S.Image src={ details.image } alt='recipe'/>

      <S.Info > 
        <S.Title>{ details.title }</S.Title>

        <S.Score percentage={ details.score }> spoonacular score.</S.Score>
        <S.Score percentage={ details.healthScore }> health score.</S.Score>

        <S.Diets>
            { details.diets.map((diet, index) => {
              return (
                <S.Diet key={ index }>{ diet }</S.Diet>
              )
            }) }
        </S.Diets>

      </S.Info>
    </S.RecipeDetail>
  )
}

