import { GET_RECIPES, SORT_RECIPES } from '../actions';

const initialState = {
  recipes: [],
  sortCriteria: 'none',
};

export default function reducer(state = initialState, action) {
  switch(action.type) {
    case GET_RECIPES: 
      return {
        ...state,
        recipes: action.payload,
      };
    case SORT_RECIPES:

      return {
        ...state,
        recipes: sortFunction([...state.recipes], action.payload),
        sortCriteria: action.payload,
      }
    default:
      return {
        ...state,
      };
  }
};

function sortFunction(array, sortCriteria) {
  switch(sortCriteria) {
    case 'az': return array.sort((x, y) => x.localeCompare(y));
    case 'za': return array.sort((x, y) => y.localeCompare(x));
    case 'score': return array.sort((x, y) => x - y);
    default: return array;
  }
}
