import React, { useEffect } from 'react'
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components'

import { getRecipeDetail } from '../actions';

export default function RecipeDetail() {
  const recipeDetail = useSelector(state => state.recipeDetail);
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getRecipeDetail(id));
  }, [])

  return (
    <Div>
      <ul>
        <li>{ recipeDetail.title }</li>
        <li>{ recipeDetail.image }</li>
        <li>{ recipeDetail.dishTypes }</li>
        <li>{ recipeDetail.diets }</li>
        <li>{ recipeDetail.summary }</li> 
        <li>{ recipeDetail.score }</li>
        <li>{ recipeDetail.healthScore }</li> 
        <li>{ recipeDetail.instructions } </li>
      </ul>
    </Div>
  )
}

const Div = styled.div`
  position: absolute;
  top: 500px;
  right: 500px;
  font-size: 30px;
  color: black;
`;
