import styled, {keyframes} from 'styled-components';
import { colors } from '../GlobalStyle.styled';
const time = '2.5';
const size = '60px';

const a = keyframes`
  00%, 50% { transform: scale(0.0) } 
`
export const Dot = styled.div`
  width: ${size}; height: ${size};
  display: inline-block;
  border-radius: 50%;
  background-color: ${colors.RED};
  animation: ${a} ${props => `${time}s ${props.i/5*time - 1}s infinite`};

  &:after {
    width: ${size}; height: ${size};
    display: inline-block;
    content: '';
    border-radius: 50%;
    background-color: white;
    animation: ${a} ${props => `${time}s ${(props.i+1)/5*time - 1}s infinite`};
  }
`
