import React from 'react';
import { useSelector } from 'react-redux';
import * as S from './CreateRecipe.styled'

export default function CreateRecipeDumb({ 
  details, errors, handleFormSubmit, handleInputChange,
  handleAddInstruction, handleRemoveInstruction, handleAddDiet, handleRemoveDiet
}) {
  const { diets } = useSelector(state => state)

  return (
    <S.Form onSubmit={ handleFormSubmit }> 
      <S.Dropdown>
        <S.DropdownButton>Diets</S.DropdownButton>
        <S.DropdownContent> 
          {
            diets?.map((diet, index) => {
              return (
                <p key={ index } onClick={ () => handleAddDiet(diet) }>{ diet }</p>
              )
            })
          }
        </S.DropdownContent>
      </S.Dropdown> 
      { 
        details.diets.map((diet, index) => {
          return (
            <p key={ index }> 
              { diet } <S.Button type='button' onClick={ () => handleRemoveDiet(index) }>-</S.Button>
            </p>
          )
        }) 
      }

      <S.Inputs > 
        <label htmlFor='title'>Title: </label>
        <S.Input maxLength='100' name='title' value={ details.title } onChange={ handleInputChange }/> 
        { errors.title && <S.Span>{ errors.title }</S.Span> }

        <label htmlFor='summary'>Summary: </label>
        <S.Input name='summary' value={ details.summary } onChange={ handleInputChange }/> 
        { errors.summary && <S.Span>{ errors.summary }</S.Span> }

        <label htmlFor='score'>Score: </label>
        <S.Input name='score' value={ details.score } onChange={ handleInputChange }/> 
        { errors.score && <S.Span>{ errors.score }</S.Span> }

        <label htmlFor='healthScore'>healthScore: </label>
        <S.Input name='healthScore' value={ details.healthScore } onChange={ handleInputChange }/> 
        { errors.healthScore && <S.Span>{ errors.healthScore }</S.Span> }

        <label htmlFor='instruction'>Add instruction:</label>
        <S.Input name='instruction' value={ details.instruction } onChange={ handleInputChange }/> 
        <S.Button type='button' onClick={ handleAddInstruction }>+</S.Button>
      </S.Inputs>

      { 
        details.instructions.map((instruction, index) => {
          return (
            <p key={ index }> 
              { instruction } <S.Button type='button' onClick={ () => handleRemoveInstruction(index) }>-</S.Button>
            </p>
          )
        }) 
      }

      <S.Button disabled={ errors.disableSubmit }>
        Submit
      </S.Button>
    </S.Form>
  )
};
