import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import styled from 'styled-components'
import axios from 'axios';

export default function RecipeDetail() {
  const [recipeDetail, setRecipeDetail] = useState({
    title: '',
    image: '',
    dishTypes: [],
    diets: [],
    summary: '',
    score: 0,
    healthScore: 0,
    instructions: [],
  });
  const { id } = useParams();

  useEffect(() => {
    (async function(id) {
      try {
        const result = await axios.get(`http://localhost:3001/recipes/${id}`); 
        setRecipeDetail(result.data);
      } catch(error) {
        console.log(error);
      }
    })(id);
  }, [])

  return (
    <Div>
      <ul>
        <li>{ recipeDetail.title }</li>

        <li><img src={ recipeDetail.image } alt='Not found.'/></li>

        <li>
          <ul> 
            { recipeDetail.dishTypes?.map((dishType, index) => {
              return (
                <li key={ index }> 
                  { dishType } 
                </li> 
              )
            }) }
          </ul>
        </li>

        <li>
          <ul> 
            { recipeDetail.diets?.map((diet, index) => {
              return (
                <li key={ index }> 
                  { id.includes('EXTERNAL_') && diet } 
                  { !id.includes('EXTERNAL_') && diet.name } 
                </li> 
              )
            }) }
          </ul>
        </li>

        <li>{ recipeDetail.summary }</li> 

        <li>{ recipeDetail.score }</li>

        <li>{ recipeDetail.healthScore }</li> 

        <li>
          <ul> 
            { recipeDetail.instructions?.map((instruction, index) => {
              return (
                <li key={ index }> 
                  { instruction } 
                </li> 
              )
            }) }
          </ul>
        </li>
      </ul>
    </Div>
  )
}

const Div = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  margin-top: -250px;
  margin-left: -500px;

  height: 500px;
  width: 1000px;
  font-size: 20px;
  background-color: pink;
`;
