import styled from 'styled-components';

export const RecipeDetail = styled.div`
	position: relative;
	display: flex;
	flex-direction: column;
	background: #FAF5E6;
	height: auto;
	width: 800px;
	margin: 85px;
	color: #2D2C2E;
	box-shadow: 10px 15px 20px 2px #222;
`

export const Image = styled.img`
	background-size: cover;
	min-height: 600px;
`

export const Info = styled.div`
	display: flex;
	flex-direction: column;
	justify-content: flex-start;
	height: 100%;
`

export const Title = styled.h1`
	margin: 40px 40px 5px;
	text-align: left;
	font-size: 1.8rem;
	font-weight: normal;
	letter-spacing: 0.1em;
	text-transform: uppercase;
`

export const Score = styled.div`
	font-size: 1.2rem;
	margin: 15px 20px;

	&:after {
		content: '★★★★★';
		padding: 5px;
		font-size: 1.4rem;
		background: ${(props) => `linear-gradient(90deg, #FBBD0D ${props.percentage}%, #2D2C2E ${props.percentage}%)`};
		background-clip: text;
		-webkit-background-clip: text;
		-webkit-text-fill-color: transparent;
}
`

export const HealthScore = styled.i`
	color: #9B9B9B;
	margin: 15px 20px;
`

export const Diets = styled.ul`
	display: flex;
	flex-wrap: wrap;
	margin: 10px 15px;
	width: auto;
`

export const Diet = styled.li`
	flex-shrink: 0;
	padding: 2px 10px;
	margin: 5px 5px;
	width: auto;
	background-color: #2D2C2E;
	color: #FAF5E6;
	border-radius: 20px;
	font-size: 0.8rem;
	text-align: center;
`

export const SubTitle = styled.h1`
	margin: 20px;
`

export const Text = styled.div`
	max-height: 250px;
	margin: 0px 10px;
	padding: 10px;
	background-color: #EAE5D6;
	overflow-y: scroll;

	::-webkit-scrollbar {
		width: 12px;
	}

	::-webkit-scrollbar-thumb {
		background-color: #2D2C2E; 
		border-radius: 10px;
	}
`
