import styled from 'styled-components';
import image from '../../images/bad.jpg'

export const LandingPage = styled.div`
  background-image: url(${image});
  height: 100vh;
  font-size: 50px;
  color: red;
`

export const Button = styled.button`
	width: 400px;
  height: 100px;
	background-color: #FD1F4A;
	color: #FAF5E6;
	letter-spacing: 0.4em;
	transition: background-color 950ms;


	&:hover {
		/* background-color: #FBBD0D; */
		/* background-color: linear-gradient(90deg, #2D2C2E 50%, #FBBD0D 50%); */
		/* -webkit-background-clip: text; */
		/* -webkit-text-fill-color: transparent; */

		background-color: #FBBD0D;
		// background-color: linear-gradient( #2D2C2E 50%, #FBBD0D 50%);
		-webkit-background-clip: text;
		-webkit-text-fill-color: transparent;
	}
	}
`
