import React, { useEffect } from 'react';
import { Route, Routes, useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import axios from 'axios';
import NavBar from './components/NavBar/NavBar.js';
import LandingPage from './components/LandingPage/LandingPage.js';
import Main from './components/Main/Main.js';
import RecipeDetail from './components/RecipeDetail/RecipeDetail.js';
import CreateRecipeSmart from './components/CreateRecipe/CreateRecipeSmart.js';
import { getDiets, getRecipes, setStatus } from './redux/actions.js';
import { GlobalStyle } from './components/GlobalStyle.styled.js';
import landingImage from './media/landing.jpg';
import createRecipeImage from './media/create-recipe.jpg';
import recipeDetailImage from './media/recipe-detail.jpg';

async function cacheImages(sources) {
}

export default function App() {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    navigate('/landing');

    (async function() {
      dispatch(setStatus('loading'));

      try {
        const dietsPromise = axios.get(`http://localhost:3001/diets`);

        const sources = [landingImage, createRecipeImage, recipeDetailImage]
        const imagesPromises = sources.map(source => {
          return new Promise((res, rej) => {
            const image = new Image();
            image.src = source;
            image.onLoad = res();
            image.onError = rej();
          })
        })

        const result = await Promise.all([dietsPromise, ...imagesPromises])

        const diets = result[1].data;

        dispatch(getDiets(diets));
      } 

      catch(error) {
        console.error(error);
      }

      dispatch(getRecipes(''));
    })()
  }, [])

  return (
    <> 
      <GlobalStyle/>
      <NavBar/>
      <Routes>
        <Route path='/landing' element={ <LandingPage/> }/>
        <Route path='/' element={ <Main/> }/>
        <Route path='/recipe/detail/:id' element={ <RecipeDetail/> }/>
        <Route path='/recipe/create' element={ <CreateRecipeSmart/> }/>
      </Routes>   
    </>

  );
};
