import React from 'react';
import { useMatch } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux'
import styled from 'styled-components'
import { filterRecipes, changeFilters, sortRecipes } from '../redux/actions.js';


export default function Filters() {
  const { diets } = useSelector(state => state)
  const { displaying }= useSelector(state => state.parameters);
  const dispatch = useDispatch();
  const match = useMatch('/')

  function handleClickFilterButton(filter) {
    dispatch(changeFilters(filter));
    dispatch(filterRecipes());
  }

  function handleClickOrderButton(sortCriteria) {
    dispatch(sortRecipes(sortCriteria));
  }


  return (
    <Form> 
      { displaying && diets.map((diet, index) => {
          return (
            <label htmlFor={ index } key={ index }>
              <input 
                key={ index }
                type='checkbox' 
                onClick={ () => handleClickFilterButton(diet) }
                style={ { height: '20px', width: '20px', } }
              />
              { diet }
              <br/>
            </label>
          )
        }) 
      }

      {match && <Dropdown>
        <DropdownButton>Order by</DropdownButton>
        <DropdownContent>
          <p onClick={() => handleClickOrderButton('az') }>(A-Z)</p>
          <p onClick={() => handleClickOrderButton('za') }>(Z-A)</p>
          <p onClick={() => handleClickOrderButton('score') }>score</p>
        </DropdownContent>
      </Dropdown> }
    </Form>
  )
};

const Form = styled.form`
  position: absolute;
  top: 300px;
  left: 0px;

  font-size: 30px;
  color: black;
  background-color: greenYellow;
`
export const DropdownContent = styled.div`
  display: none;
  top: 60px;
  position: absolute;
  background-color: #f1f1f1;
  min-width: 160px;
  box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
  z-index: 1;

  p {
  color: black;
  padding: 12px 16px;
  text-decoration: none;
  display: block;

  &:hover {background-color: #ddd;}
  }
`

export const Dropdown = styled.div`
  position: absolute;
  right: 120px;
  display: inline-block;

  &:hover ${DropdownContent} {display: block;}
`

export const DropdownButton = styled.div`
  background-color: #04AA6D;
  color: white;
  padding: 16px;
  font-size: 16px;
  border: none;
  width: 80px;
  height: 28px;

  &:hover {background-color: #3e8e41;}
`
