import React from 'react';
import { useSelector, useDispatch } from 'react-redux'
import styled from 'styled-components'
import { filterRecipes, changeFilters } from '../redux/actions.js';


export default function Filters() {
  const { diets } = useSelector(state => state)
  const { displaying }= useSelector(state => state.parameters);
  const dispatch = useDispatch();

  function handleClick(filter) {
    dispatch(changeFilters(filter));
    dispatch(filterRecipes());
  }

  return (
    <Form> 
      { displaying && diets.map((diet) => {
          return (
              <input 
                type='checkbox' 
                onClick={ () => handleClick(diet) }
                style={ { height: '20px', width: '20px', } }
              />
          )
        }) 
      }
    </Form>
  )
};

const Form = styled.form`
  position: absolute;
  top: 300px;
  left: 0px;

  font-size: 30px;
  color: black;
  background-color: greenYellow;
`
