import styled from 'styled-components';
import image from '../../images/good.jpg'

export const LandingPage = styled.div`
  background-image: url(${image});
  // background: center / cover;
  height: 100vh;
	display: flex;
	flex-direction: column;
	justify-content: flex-end
`

export const Button = styled.button`
	margin: 200px;
	width: 400px;
  height: 100px;
	font-size: 1.2rem;
	letter-spacing: 0.4em;
	background-color: #FD1F4A;
	color: #FAF5E6;
	box-shadow: 10px 10px 20px 10px rgba(0,0,0,1);
	transition: background-color 950ms;


	&:hover {
		/* background-color: #FBBD0D; */
		/* background-color: linear-gradient(90deg, #2D2C2E 50%, #FBBD0D 50%); */
		/* -webkit-background-clip: text; */
		/* -webkit-text-fill-color: transparent; */

		background-color: #FBBD0D;
		// background-color: linear-gradient( #2D2C2E 50%, #FBBD0D 50%);
		-webkit-background-clip: text;
		-webkit-text-fill-color: transparent;
	}
`
