import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';
import { useStore, useDispatch } from 'react-redux';
import styled from 'styled-components';

import { postRecipe } from '../actions';

export default function CreateRecipe() {
  const recipeDetail = useStore(state => state.recipeDetail);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [details, setDetails] = useState({
    title: '',
    summary: '',
    score: '',
    healthScore: '',
    instructions: '',
  });

  function handleChange(event) {
    setDetails({
      ...details,
      [event.target.name]: event.target.value,
    })
  } 

    function handleSubmit(event) {
      event.preventDefault();
      console.log(details)
      dispatch(postRecipe(details));
      setDetails({
        ...details,
        title: '',
        summary: '',
        score: '',
        healthScore: '',
        instructions: '',
      })
      console.log(recipeDetail.id);
      // navigate(`/recipe/detail/${ recipeDetail.id }`)
    }

  return (
    <Form onSubmit={ handleSubmit }> 
      <label htmlFor='title'>Title: </label>
      <Input name='title' value={ details.title } onChange={ handleChange }/> 

      <label htmlFor='summary'>Summary: </label>
      <Input name='summary' value={ details.summary } onChange={ handleChange }/> 

      <label htmlFor='score'>Score: </label>
      <Input name='score' value={ details.score } onChange={ handleChange }/> 

      <label htmlFor='healthScore'>healthScore: </label>
      <Input name='healthScore' value={ details.healthScore } onChange={ handleChange }/> 

      <label htmlFor='instructions'>Instructions: </label>
      <Input name='instructions' value={ details.instructions } onChange={ handleChange }/> 

      <Button>
        Submit
      </Button>
    </Form>
  )
};

const Form = styled.form`
  position: relative;
  top: 200px;
  left: 500px;

  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  height: 400px;
  width: 1000px;
  background-color: cyan;
`;

const Input = styled.input`
  display: block;
  position: relative;

  height: 50px;
  width: 200px;
  color: black;
  border: solid black;
  margin: 10px;
`

const Button = styled.button`
  position: relative;

  height: 50px;
  width: 50px;
  color: black;
  border: solid black;
  background-color: violet;
  margin: 10px;
  padding: 10px;
`
