import React from 'react';
import { Link } from 'react-router-dom';
import * as S from './Recipe.styled.js';

export default function Recipe(props) {

   return (
      <S.Recipe>
         <S.Image src={ props.image } alt='recipe' />

         <S.Info> 
            <S.Title>{ props.title.length > 22 ? props.title.slice(0,22) + '...' : props.title }</S.Title> 

            <S.Score>
               { props.score }
            </S.Score>

            <S.Diets>
               { props.diets.map((diet, index) => {
               return (
                  <S.Diet key={ index }>{ diet }</S.Diet>
               )
               }) }
            </S.Diets>
         </S.Info>


         <Link to={ `/recipe/detail/${ props.id }` }>
            <S.DetailButton>
               LET'S COOK!
            </S.DetailButton>
         </Link>
      </S.Recipe>
   )
};




<div class="card">
   <div class="header">
      <div class="icon">
         <a href="#"><i class="fa fa-heart-o"></i></a>
      </div>
   </div>
   <div class="text">
      <h1 class="food">
         Chinese Noodles
      </h1>
      <i class="fa fa-clock-o"> 15 Mins</i>
      <i class="fa fa-users"> Serves 2</i>

      <p class="info">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Earum, temporibus.</p>
   </div>
   <button>Let's Cook!</button>
</div>
