import styled from 'styled-components';
import { colors } from '../GlobalStyle.styled';

export const DisplayParameters = styled.div`
  min-height: 1020px;
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  font-size: 20px;
  color: ${colors.WHITE};
`

export const Filters = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  box-shadow: 0px 8px 16px 0px rgba(55,55,55,0.8);
`

export const FiltersTitle = styled.h1`
  margin: 30px 30px 40px;
  text-align: center;
  font-size: 32px;
`

export const Filter = styled.span`
  cursor: pointer;
  height: 36px;
  margin: 9px;
  padding: 6px 16px;
  border-radius: 80px;
  text-align: center;
  background-color: ${props => props.active ? colors.RED : colors.BLACK};
  transition: 100ms;

  &:hover {
    opacity: 0.8;
  }
`

export const DropdownButton = styled.div`
  height: 55px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 28px;
  font-weight: 600;
  box-shadow: 0px 4px 8px 0px rgba(55,55,55,0.8);
  background-color: ${colors.BLACK};
  color: ${colors.WHITE};
  transition: 200ms;

  &:hover {
    opacity: 0.6;
  }
`

export const DropdownContent = styled.div`
  visibility: hidden;
  height: 0;
  opacity: 0;
  box-shadow: 0px 8px 16px 0px rgba(55,55,55,0.8);
  color: ${colors.WHITE};
  transition: visibility 200ms, opacity 200ms linear;

  p {
    padding: 12px 16px;
    font-size: 22px;
    font-weight: 600;

    &:hover {
      background-color: ${colors.WHITE};
      color: ${colors.BLACK};
    }
  }
`

export const OrderBy = styled.div`
  align-self: flex-start;
  margin: 25px 0px;
  width: 195px;

  &:hover ${DropdownContent} {
    visibility: visible;
    height: auto;
    opacity: 1;
  }

  &:hover ${DropdownButton} {
    box-shadow: 0px 0px 0px 0px;
  }
`
